        JOIN, 
        GET, 
        PAYBACK, 
        CANCELED,
        ENDED
    }

    // enum Network { HARDHAT, CELO, CROSSFI }

    enum Phase { ALPHA, MAINNET }

    enum Status { AVAILABLE, TAKEN }

    enum Branch { CURRENT, RECORD }

    enum Router { NONE, PERMISSIONLESS, PERMISSIONED }

    struct Pool {
        Low low;
        Big big;
        Addresses addrs;
        Router router;
        Stage stage;
        Status status;
    }

    struct Low {
        uint8 maxQuorum;
        uint8 selector;
        uint24 colCoverage;
        uint32 duration;
        uint8 allGh;
        uint8 userCount;
    }

    struct Big {
        uint256 unit;
        uint256 currentPool;
        uint96 recordId;
        uint96 unitId;
    }

    struct Point {
        uint contributor;
        uint creator;
        uint referrals;
        address user;
        Phase phase;
    }

    struct PointsReturnValue {
        string key;
        Point[] value;
    }

    struct Interest {
        uint fullInterest;
        uint intPerSec;
    }

    /**
     * @notice Structured types - Address
     * @param asset : Contract address of the asset in use.
     * @param lastPaid: Last contributor who got finance.
     * @param safe : Strategy for each pool or epoch. See Strategy.sol for more details.
     * @param admin : Pool creator.
     * 
    */
    struct Addresses {
        IERC20 colAsset;
        address lastPaid;
        address safe;
        address admin;
    }

