        uint32 paybackTime;
        uint32 turnStartTime;
        uint32 getFinanceTime;
        uint loan;
        uint colBals;
        address id;
        bool sentQuota;
    }

    struct Price {
        uint128 price;
        uint8 decimals;
    }

    struct Provider {
        uint slot;
        uint amount;
        uint rate;
        uint earnStartDate;
        address account;
        Interest accruals;
    }

    struct Payback_Safe {
        address user; 
        IERC20 baseAsset; 
        uint256 debt;
        uint256 attestedInitialBal;
        bool allGF; 
        Contributor[] cData;
        bool isSwapped;
        address defaulted;
        uint96 recordId;
        IERC20 collateralAsset;
    }

    struct Slot {
        uint value;
        bool isMember;
        bool isAdmin;
    }

    struct ReadPoolDataReturnValue {
        Pool pool;
        ContributorReturnValue[] cData;
    }

    struct ReadRecordDataReturnValue {
        Pool pool;
        Contributor[] cData;
    }

    struct UpdatePoolData {
        uint unit;
        uint96 unitId;
        uint96 recordId;
        uint8 maxQuorum;
        uint24 colCoverage;
        IERC20 colAsset;
        uint16 durationInHours;
        address creator;
        Router router; 
    }

    struct Analytics {
        uint256 tvlCollateral;
        uint256 tvlBase;
        uint totalPermissioned;
        uint totalPermissionless;
    }

    struct ViewFactoryData {
        Analytics analytics;
        uint16 makerRate;
        uint currentEpoches;
        uint recordEpoches;
    }

    struct ContributorReturnValue {
        Contributor profile;
        Slot slot;
        Common.Provider[] providers;
    }

