pragma solidity 0.8.24;

import { OnlyRoleBase, IRoleBase } from "../peripherals/OnlyRoleBase.sol";
import { ISupportedAsset } from "../interfaces/ISupportedAsset.sol"; 
import { IERC20 } from "../interfaces/IERC20.sol"; 
import { ErrorLib } from "../libraries/ErrorLib.sol";
import { IPriceOracle } from "../interfaces/IPriceOracle.sol";

abstract contract SupportedAssetManager is ISupportedAsset, IPriceOracle, OnlyRoleBase {
  using ErrorLib for *;

  // The max acceptable amount of time passed since the oracle price was last updated.
  uint128 public maxTimePassed;

  // Supported assets
  SupportedAsset[] private assets;

  /**
   * @dev Mapping assets address to bool i.e Assets must be contract account
   * and must be supported
   */
  mapping(address => bool) private supportedAssets;

  mapping(address => bool) public listed;

  /**
   * @dev Asset must be supported before they can be used.
   */
  modifier onlySupportedAsset(address _asset) {
    if(!supportedAssets[_asset]) 'Unsupported asset'._throw();
    _;
  }

  /**
   * 
   * @param _assets : Supported assets
   * @param _roleManager : Role manager contract
  */
  constructor(
    address[] memory _assets,
    IRoleBase _roleManager
  )
    OnlyRoleBase(_roleManager) 
  {
    for(uint i = 0; i < _assets.length; i++) {
      if(_assets[i] != address(0)) _supportAsset(_assets[i]);
    }
    maxTimePassed = 120 seconds;
  }

  /**
   * @dev Support a new asset
   * Note: OnlyRoleBase action
   * @param _asset : Asset to add to list of supported asset
   */
  function supportAsset(address _asset) 
    public 
    onlyRoleBearer
  {
    _supportAsset(_asset); 
  }

  function _supportAsset(address _asset) private {
    if(!listed[_asset]){
      listed[_asset] = true;
      assets.push(SupportedAsset(
        _asset, 
        IERC20(_asset).name(), 
        IERC20(_asset).symbol()
      ));
    }
    if(!_isAssetSupported(_asset)){
      supportedAssets[_asset] = true;
    }
  }

  /**
   * @dev Unsupports an asset
   * Note: Only-owner action
   * @param newAsset : Removes an asset from the list of supported asset
   */
  function unsupportAsset(
    address newAsset
  ) 
    public 
    onlyRoleBearer
  {
    supportedAssets[newAsset] = false;
  }

  function _isAssetSupported(address _asset) internal view returns(bool) {
    return supportedAssets[_asset];
  }

  /**
   * @dev Check if an asset is supported
   */
  function isSupportedAsset(address _asset) external view returns(bool) {
    return _isAssetSupported(_asset);
  }

  /**
   * @dev Returns a list of supported assets
   */
  function getSupportedAssets() public view returns(SupportedAsset[] memory _assets) {
    _assets = assets;
    return _assets;
  }

  function getDefaultSupportedCollateralAsset() external view returns(address){
    return assets[0].id;
  }

  /**
   * @dev Update the maxTimePass
   * @param newMaxTimePass : New maxTime
   */
  function setMaxTimePass(uint128 newMaxTimePass) public onlyRoleBearer returns(bool) {
    maxTimePassed = newMaxTimePass;
    return true;
  }

}","keccak256":"0x5b980c099dcbf25c8ebfc5f3dbc4a715a65babb46338700a7bcf28774ef3479d","license":"MIT"},"contracts/standalone/oracles/CeloSupportedAssetManager.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import { SupportedAssetManager, IRoleBase } from "../../peripherals/SupportedAssetManager.sol";
import { AggregatorV3Interface } from "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";

contract CeloSupportedAssetManager is SupportedAssetManager {
  /**
   * @notice Each asset pair on chainlink maintains a price oracle contract address
   */
  mapping(address => PriceData) private data;

  /**
   * 
   * @param collateralAssets : Supported assets
   * @param _roleManager : Role manager contract
   * @param oracleData : Celo oracle data: optional 
   * @notice Ensure the length of collateralAssets matches that of celoOracleData.
  */
  constructor(
    address[] memory collateralAssets,
    IRoleBase _roleManager,
    PriceData[] memory oracleData
  )
    SupportedAssetManager(collateralAssets, _roleManager) 
  {
    require(collateralAssets.length == oracleData.length, "Oracle keys & values mismatch");
    for(uint i = 0; i < collateralAssets.length; i++) {
      address asset = collateralAssets[i];
      data[asset] = oracleData[i];
      // if(i == 1) {
      //   // _updatePriceFeed(asset);
      // }
    }
  }

  /**
  * @dev A function that retreives the price and the corresponding timestamp
  * from the price oracle and save it in the state
  * @param asset : Asset
  * @param _data : Mutable oracle data.
  */ 
  function _getPriceInfo(address asset, PriceData memory _data) internal returns(uint128 price) {
    assert(asset != address(0));
    (,int256 answer,,uint256 timestampOflatestPrice,) = AggregatorV3Interface(_data.oracleAddress).latestRoundData();
    _data.latestPrice = uint128(uint(answer));
    _data.timestampOflatestPrice = uint128(uint(timestampOflatestPrice));
    data[asset] = _data;
    price = _data.latestPrice;
  }

  /**
  * @dev A function that checks if the timestamp of the saved price
  * is older than maxTimePassed.
  * @param timestampOflatestPrice: The time price was last updated. 
  * @return inTime - A bool that will be true if the price was updated
  * at most maxTimePassed seconds ago, otherwise false.
  */ 
  function _checkPriceAge(uint128 timestampOflatestPrice) internal view returns (bool inTime){
    inTime = (block.timestamp - timestampOflatestPrice) < maxTimePassed;
  } 

  /**
   * @dev Get price data into external contracts
