    // enum Network { HARDHAT, CELO, CROSSFI }

    enum Phase { ALPHA, MAINNET }

    enum Status { AVAILABLE, TAKEN }

    enum Branch { CURRENT, RECORD }

    enum Router { NONE, PERMISSIONLESS, PERMISSIONED }

