pragma solidity 0.8.24;

import { IERC20 } from './IERC20.sol';

interface Common {
    event PoolCreated(Pool);
    event NewContributorAdded(Pool);
    event GetFinanced(Pool);
    event Payback(Pool);
    event Liquidated(Pool);
    event Cancellation(uint unit);
    event PoolEdited(Pool);

    enum Stage {
        JOIN, 
        GET, 
        PAYBACK, 
        CANCELED,
        ENDED
    }

    enum Phase { BETA, ALPHA, MAINNET }

    enum Status { AVAILABLE, TAKEN }

    enum Branch { CURRENT, RECORD }

    enum Router { NONE, PERMISSIONLESS, PERMISSIONED }

    struct Pool {
        Low low;
        Big big;
        Addresses addrs;
        Router router;
        Stage stage;
        Status status;
    }

    struct Low {
        uint8 maxQuorum;
        uint8 selector;
        uint24 colCoverage;
        uint32 duration;
        uint8 allGh;
        uint8 userCount;
    }

    struct Big {
        uint256 unit;
        uint256 currentPool;
        uint96 recordId;
        uint96 unitId;
    }

    struct Point {
        uint contributor;
        uint creator;
        uint referrals;
        address user;
        Phase phase;
    }

    struct PointsReturnValue {
        string key;
        Point[] value;
    }

    struct Interest {
        uint fullInterest;
        uint intPerSec;
    }

    /**
     * @notice Structured types - Address
     * @param asset : Contract address of the asset in use.
     * @param lastPaid: Last contributor who got finance.
     * @param safe : Strategy for each pool or epoch. See Strategy.sol for more details.
     * @param admin : Pool creator.
     * 
    */
    struct Addresses {
        IERC20 colAsset;
        address lastPaid;
        address safe;
        address admin;
    }

    /**
     *  @param isMember : Whether user is a member or not
     *  @param turnStartTime: Time when the contributor's turn start to count.
     *  @param getFinanceTime: Date when loan was disbursed
     *  @param paybackTime: Date which the borrowed fund must be retured
     *  @param loan: Total debts owed by the last fund recipient.
     *  @param colBals: Collateral balances of the last recipient.
     *  @param sentQuota : Whether an user/current msg.sender has received or not.
     *  @param id : Address of the last recipient.
     * @param interestPaid : The amount of interest paid  
    */
    struct Contributor {
        uint32 paybackTime;
        uint32 turnStartTime;
        uint32 getFinanceTime;
        uint loan;
        uint colBals;
        address id;
        bool sentQuota;
    }

    struct Price {
        uint128 price;
        uint8 decimals;
    }

    struct Provider {
        uint slot;
        uint amount;
        uint rate;
        uint earnStartDate;
        address account;
        Interest accruals;
    }

    struct Payback_Safe {
        address user; 
        IERC20 baseAsset; 
        uint256 debt;
        uint256 attestedInitialBal;
        bool allGF; 
        Contributor[] cData;
        bool isSwapped;
        address defaulted;
        uint96 recordId;
        IERC20 collateralAsset;
    }

    struct Slot {
        uint value;
        bool isMember;
        bool isAdmin;
    }

    struct ReadPoolDataReturnValue {
        Pool pool;
        ContributorReturnValue[] cData;
    }

    struct ReadRecordDataReturnValue {
        Pool pool;
        Contributor[] cData;
    }

    struct UpdatePoolData {
        uint unit;
        uint96 unitId;
        uint96 recordId;
        uint8 maxQuorum;
        uint24 colCoverage;
        IERC20 colAsset;
        uint16 durationInHours;
        address creator;
        Router router; 
    }

    struct Analytics {
        uint256 tvlCollateral;
        uint256 tvlBase;
        uint totalPermissioned;
        uint totalPermissionless;
    }

    struct ViewFactoryData {
        Analytics analytics;
        uint16 makerRate;
        uint currentEpoches;
        uint recordEpoches;
    }

    struct ContributorReturnValue {
        Contributor profile;
        Slot slot;
        Common.Provider[] providers;
    }

}","keccak256":"0x47a3d0ec1786098932782f83fc4a146e39ec23e4c1f7b47b4618a3a421cae181","license":"MIT"},"contracts/apis/IERC20.sol":{"content":"// SPDX-License-Identifier: MIT
