pragma solidity 0.8.24;

import { IOwnerShip } from "../apis/IOwnerShip.sol";
/**
 * @title MsgSender 
 * @author Simplifi (Bobeu)
 * @notice Non-deployable contract simply returning the calling account.
 */
abstract contract MsgSender {
    function _msgSender() internal view returns(address sender) {
        sender = msg.sender;
    }
}

/**
 * @title OwnerShip 
 * @author Simplifi (Bobeu)
 * @notice Standalone contract for managing ownership in Simplifi protocol.
 */
contract OwnerShip is IOwnerShip, MsgSender{
    /**
     * @notice Number of owners.
     */
    uint public ownersCount;

    /**
     * @notice Addresses with ownership permission.
     */
    
    mapping (address => bool) private _isOwner;

    /**
     * @notice Mapping of ownersCount to addresses.
     * A valid id will return a mapped owner.
     */
    mapping (uint => address) public owners;

    /**
     * @dev Only owner is allowed.
     */
    modifier onlyOwner {
        require(_isOwner[_msgSender()], "Oop! Caller is not recognized");
        _;
    }

    constructor() {
        _setOwner(_msgSender(), true);
    }

    /**
     * @dev Returns owner variable.
     */
    function _getOwner(uint ownerId) 
        internal 
        view 
        returns(address _owner) 
    {
        _owner = owners[ownerId];
    }

    /**
     * @dev Add or remove target address as owner.
     * @param target: Target address.
     * @notice 'target' parameter must not be empty.
     */
    function _setOwner(
        address target,
        bool add
    ) 
        private 
    {
        require(target != address(0), "Simplifi OwnerShip: 'target' parameter is empty");
        add? (_isOwner[target] = true, ownersCount ++) : (_isOwner[target] = false, ownersCount --);
    }

    /**
     * @dev Add a new owner address
     * @param newOwners: New owners
     * @notice Only address with owner permission can add another owner.
     */
    function setPermission(
        address[] memory newOwners
    ) 
        external
        onlyOwner
        returns(bool) 
    {
        bool rt = true;
        for(uint r = 0; r < newOwners.length; r++) {
            _setOwner(newOwners[r], rt);
        }
        return rt;
    }

    /**
     * @dev Remove an address as owner.
     * @param target: Target address
     * @notice Only address with owner permission can remove another owner.
     */
    function removeOwner(
        address target
    ) 
        external
        onlyOwner
        returns(bool) 
    {
        _setOwner(target, false);
        return true;
    }
    /**
     * @dev An owner can renounce their ownership. This however will not leave the
     * contract empty without an owner. There must be at least one owner left.
     * @notice Only address with owner permission can renounce ownership.
     */
    function renounceOwnerShip() 
        external
        onlyOwner
        returns(bool) 
    {
        require(ownersCount > 1, "At least 2 owners is required to leave"); 
        _setOwner(_msgSender(), false);
        return true;
    }

    /**
     * @dev Returns owner variable.
     * Can be called externally by contracts.
     * @param ownerId : Owner Id. 
     */
    function getOwner(
        uint ownerId
    ) 
        external 
        view 
        returns(address) 
    {
        return _getOwner(ownerId);
    }

    /**
     * @dev Check if target is an owner.
     * @param target : Target address.
     */
    function isOwner(
        address target
    )
        external
        view 
        returns(bool) 
    {
        return _isOwner[target];
    }
}","keccak256":"0xecca6e7a2c2684e193fcb16ac131d1b2895f981862969101d96d94616db70c0c","license":"MIT"},"contracts/implementations/strategies/Strategy.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import { SafeMath } from "@thirdweb-dev/contracts/external-deps/openzeppelin/utils/math/SafeMath.sol";
import { SafeCallERC20, IERC20 } from "../../libraries/SafeCallERC20.sol";
import { IStrategy } from "../../apis/IStrategy.sol";
import { Common } from "../../apis/Common.sol";
import { OnlyOwner } from "../../abstracts/OnlyOwner.sol";

contract Strategy is IStrategy, OnlyOwner {
  using SafeMath for uint;

  /**
   * @dev Approvals in native coin i.e XFI 
   * mapping of providera to balances.
   */
  mapping(address => mapping (uint => uint)) private nativeApprovals;

  /**
   * @dev mapping of epoch Id to assets.
   * Each epoch might use different asset as constribution base i.e
   * A band creator might want to contribute in USDT while another 
   * might choose USDC. The base point is that any asset in use must
   * conform to ERC20 standard.
   */
  mapping (uint => address) public assets;

  /**
   * @notice Mapping of epoches to list of contributors.
   */
  mapping (uint => address[]) public contributors;

  /**
   * @notice Every loan repayment is recorded here
   * Mapping of epochId to credit balances.
   * Using this method to track amortization records will enables us 
   * to properly account for loans and interests payments from all 
   * contributors in an epoch. 
   */
  mapping (uint => uint) public credits;

  /**
   * @dev Initializes state variables.
   * OnlyOwner function.
   */
  constructor (address _ownershipManager) OnlyOwner(_ownershipManager)  {}

  receive() external payable onlyOwner("Strategy: Not received - Not permitted") {}

  /**
   * @dev Implementation of IStrategy.addUp
   * See IStrategy.addUp for doc
  */
  function addUp(
    address user,
    uint epochId
  ) 
    external
    onlyOwner("Strategy - addUp: Not permitted")
    returns(bool)
  {
    contributors[epochId].push(user);
    return true;
  }

  /**
   * @dev Implementation of IStrategy.mapAsset
   * See IStrategy.mapAsset for doc
   */
  function mapAsset(
    uint epochId, 
    address assetInUse
  ) 
    external
    onlyOwner("Strategy - mapAsset: Not permitted")
    returns(bool) 
  {
    assets[epochId] = assetInUse;
    return true;
  }

  /**
   * @notice Return outstanding allowance
   * @param asset : Asset in use
   * @param user : provider
   */
  function _prevAllowance(
    address asset, 
    address user
  ) 
    internal 
    view returns(uint allowance) 
  {
    allowance = IERC20(asset).allowance(address(this), user);
  }
  
  /**
   * @dev Implementation of IStrategy.setClaim
   * See IStrategy.setClaim for doc.
   * @notice 'credit' should only be set if borrower is returning the borrowed fund.
  */ 
  function setClaim(
    uint claim,
    uint fee,
    uint credit,
    uint epochId,
    address user,
    address feeTo, 
    bool allHasGF,
    Common.TransactionType txType
  ) 
    external
    payable
    onlyOwner("Strategy - setClaim: Not permitted")
    returns(uint actualClaim)
  {
    address asset = assets[epochId];
    if(txType == Common.TransactionType.ERC20) {
      actualClaim = claim > fee? claim - fee : claim;
      _setAllowance(user, asset, actualClaim);
      if(fee > 0) {
        IERC20(asset).transfer(feeTo, fee);
      }
    } else {
      nativeApprovals[user][epochId] += claim;
      if(credit > 0) credits[epochId] += credit;
      if(allHasGF) {
        _roundUp(epochId, assets[epochId]); //-------------------------------------hetr
      }
    }
    return actualClaim;
  }

  /**
   * @notice Closes the current epoch and set claims for all contributors 
   *          of this epoch.
   * @param epochId : Epoch Id.
   * @param asset : Asset in use
   */
  function _roundUp(
    uint epochId,
    address asset
  ) 
    private 
  {
    uint credit = credits[epochId];
    address[] memory providers = contributors[epochId];
    // credits[epochId] = credit.sub(credit);
    credits[epochId] = 0;
    uint size = providers.length;
    // if(credit < size) revert InsufficientCredit(credit, size);
    assert(credit > size);
    uint allowance = credit.div(size);
    for(uint i = 0; i < size; i++){
      _setAllowance(providers[i], asset, allowance);
    }
  }

  /**
   * @dev Approve spender contributor 'to' to spend from contract's balance
   * @param to : Contributor
   * @param asset : Currency in use
   * @param amount : Value
   */
  function _setAllowance(
    address to, 
    address asset, 
    uint256 amount
  ) 
    private 
  {
    uint prevAllow = _prevAllowance(asset, to);
    IERC20(asset).approve(to, prevAllow > 0? amount + prevAllow : amount);
  }

  /**
   *  @dev Withdraw Native coin. 
   */
  function withdraw(
    uint epochId,
    address user
  ) 
    external 
    onlyOwner("Strategy: Not Permitted")
    returns(uint xfiBalances) 
  {
    xfiBalances = nativeApprovals[user][epochId];
    nativeApprovals[user][epochId] = 0;
    require(xfiBalances > 0, "No claim");
    if(address(this).balance == 0) {
      revert InsufficientNativeBalanceInContract(address(this).balance);
    }
    payable(user).transfer(xfiBalances);
    // (bool sent,) = sender.call{value: balance}("");
    // require(sent,"Op failed");
    return xfiBalances;
  }

  /**
   * @dev Swaps addresses. Only during liquidation. We attemp to replace
   *  the defaulted party with the liquidated.
   * @param epochId : EpochId.
   * @param newProv : New address.
   * @param oldProv : Old address.
   */
  function swapProvider(
    uint epochId, 
    address newProv, 
    address oldProv
  ) 
    external 
    onlyOwner("Strategy - swapProvider: Not permitted")
    returns(bool success) 
  {
    address[] memory addrs = contributors[epochId]; 
    uint slot;
    for(uint i = 0; i < addrs.length; i++) {
      if(addrs[i] == oldProv) {
        slot = i;
        success = true;
      }
    }
    if(success) {
      contributors[epochId][slot] = newProv;
    }
    return success;
  }

}","keccak256":"0xc879f16822445f2153962972bf3d910bcf4358b15553eb478b8f187a30c0f8b8","license":"MIT"},"contracts/implementations/strategies/StrategyManager.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import { IStrategyManager } from "../../apis/IStrategyManager.sol";
import { Strategy } from "./Strategy.sol";
import { OnlyOwner } from "../../abstracts/OnlyOwner.sol";

/**@title SmartStrategyAdmin: A standalone contract that manages strategy creation, 
   deletion, read and write data.

   Author: Simplifinance
 */
contract StrategyManager is IStrategyManager, OnlyOwner {
  // using Clones for address;

  // Strategy count 
  uint public totalStrategies;

/**
 * @dev List of Strategies and their keys 
 */
  StrategyData[] private strategies;

 /**
 * @dev Mapping of addresses to strategies.
 * Also used as reverse map of strategies to status.
 */
  mapping(address => address) private strategyMap;

  constructor (
    address _ownershipManager
  ) OnlyOwner(_ownershipManager) {}

  receive() 
    external 
    payable 
  {
    revert();
  }
  
  /**@dev Return if account owns a strategy or not
  */
  function _hasStrategy(
    address user
  ) 
    internal 
    view 
    returns (bool) 
  {
    return strategyMap[user] != address(0);
  }

  // Returns smartStrategy for 'user'
  function _getStrategy(
    address user
  ) 
    internal 
    view returns(address) 
  { 
    return strategyMap[user];
  }
  
  /**@dev Create a new strategy.
   * @notice 'user' should not own a strategy before now.
   *          only address with owner permission can call.
  */
  function createStrategy(
    address user
  )
    external
    onlyOwner("StrategyMgr - createStrategy: Not permitted")
    returns(address _strategy) 
  {
    if(!_hasStrategy(user)){
      _strategy = _createStrategy(user);
    } else {
      _strategy = _getStrategy(user);
    }
    return _strategy;
  }

  /**
   * @dev Create strategy - private function
    * @param caller : msg.sender
   * 
   * @notice Even if user is trying to rekey or upgrade smartstrategy, same amount of fee is required
   * for successful upgrade.
   */
  function _createStrategy(
    address caller
  ) 
    private 
    returns(address strategy) 
  {
    totalStrategies ++;
    // address ssi = instance;
    // strategy = ssi.cloneDeterministic(keccak256(abi.encodePacked(totalStrategies, caller)));
    strategy = address(new Strategy(ownershipManager));
    _updateStrategy(caller, strategy);
  }

  /**
   * Update storage with the new Strategy instance : {internal}
   * @param user : User/Caller address 
   * @param strategy : New Strategy address
   */
  function _updateStrategy(
    address user, 
    address strategy
  ) 
    private 
  {
    strategyMap[user] = strategy;
  }

  /// Returns strategy of 'user'
  /// @param user : User Address
  function getStrategy(
    address user
  ) 
    external 
    view 
    returns(address) 
  { 
    return _getStrategy(user);
  }
}
