    address assetAdmin;
    uint16 makerRate;
    address bankFactory;
  }

  struct Analytics {
    uint256 tvlInXFI;
    uint256 tvlInUsd;
    uint totalPermissioned;
    uint totalPermissionless;
  }

  struct ViewFactoryData {
    Analytics analytics;
    ContractData contractData;
  }
}

