pragma solidity 0.8.24;

import { IRoleBase } from "../apis/IRoleBase.sol";
import { ErrorLib } from "../libraries/ErrorLib.sol";

/**
 * @title MsgSender 
 * @author Simplifi (Bobeu)
 * @notice Non-deployable contract simply returning the calling account.
 */
abstract contract MsgSender {
    function _msgSender() internal view virtual returns(address sender) {
        sender = msg.sender;
    }
}

abstract contract OnlyRoleBase is MsgSender {
    using ErrorLib for *;

    // Role manager address
    IRoleBase public roleManager;

    // ============= constructor ============
    constructor(IRoleBase _roleManager)
    {
        _setRoleManager(_roleManager);
    }

    /**
     * @notice Caller must have owner role before execeution can proceed.
     * The 'errorMessage' argument can be used to return error specific to 
     * a context e.g function call. 
     */
    modifier onlyRoleBearer {
        _onlyRoleBearer();
        _;
    }

    // Allow only account with role access
    function _onlyRoleBearer() internal view {
        IRoleBase mgr = roleManager;
        if(address(mgr) == address(0)) 'Manager is zero'._throw();
        if(!_hasRole(_msgSender())) 'Access denied'._throw();
    }

    function _hasRole(address target) internal view returns(bool result) {
        result = IRoleBase(roleManager).hasRole(target);
    }  

    /// @dev Set role manager
    function _setRoleManager(IRoleBase newManager) private{
        roleManager = newManager;
    }

    /**
     * Set Role manager
     * @param newManager : New manager address
     */
    function setRoleManager(
        address newManager
    )
        public
        onlyRoleBearer
        returns(bool)
    {
        _setRoleManager(IRoleBase(newManager));
        return true;
    }
}","keccak256":"0xa16a1ee72f3b6ef52356210cdc85a46ddd5a88d56c0c044c0740b68cf5b83438","license":"MIT"},"contracts/standalone/SupportedAssetManager.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import { OnlyRoleBase, IRoleBase } from "../peripherals/OnlyRoleBase.sol";
import { ISupportedAsset } from "../apis/ISupportedAsset.sol"; 
import { IERC20 } from "../apis/IERC20.sol"; 
import { ErrorLib } from "../libraries/ErrorLib.sol";

contract SupportedAssetManager is ISupportedAsset, OnlyRoleBase {
  using ErrorLib for *;

  struct SupportedAsset {
    address id;
    string name;
    string symbol;
  }

  // Supported assets
  SupportedAsset[] private assets;

  /**
   * @dev Mapping assets address to bool i.e Assets must be contract account
   * and must be supported
   */
  mapping(address => bool) private supportedAssets;

  mapping(address => bool) public listed;

  /**
   * @dev Asset must be supported before they can be used.
   */
  modifier onlySupportedAsset(address _asset) {
    if(!supportedAssets[_asset]) 'Unsupported asset'._throw();
    _;
  }

  /**
   * @dev Initialize state variables
   * @param _asset : Initial supported asset
   */
  constructor(
    address _asset,
    IRoleBase _roleManager
  ) 
    OnlyRoleBase(_roleManager) 
  {
    if(_asset == address(0)) 'Default asset is empty'._throw();
    _supportAsset(_asset);
  }

  /**
   * @dev Support a new asset
   * Note: OnlyRoleBase action
   * @param _asset : Asset to add to list of supported asset
   */
  function supportAsset(
    address _asset
  ) 
    public 
    onlyRoleBearer
  {
    _supportAsset(_asset); 
  }

  function _supportAsset(address _asset) private {
    
    if(!listed[_asset]){
      listed[_asset] = true;
      assets.push(SupportedAsset(
        _asset, 
        IERC20(_asset).name(), 
        IERC20(_asset).symbol()
      ));
    }
    if(!_isAssetSupported(_asset)){
      supportedAssets[_asset] = true;
    }
  }

  /**
   * @dev Unsupports an asset
   * Note: Only-owner action
   * @param newAsset : Removes an asset from the list of supported asset
   */
  function unsupportAsset(
    address newAsset
  ) 
    public 
    onlyRoleBearer
  {
    supportedAssets[newAsset] = false;
  }

  function _isAssetSupported(address _asset) internal view returns(bool) {
    return supportedAssets[_asset];
  }

  /**
   * @dev Check if an asset is supported
   */
  function isSupportedAsset(address _asset) external view returns(bool) {
    return _isAssetSupported(_asset);
  }

  /**
   * @dev Returns a list of supported assets
   */
  function getSupportedAssets() public view returns(SupportedAsset[] memory _assets) {
    _assets = assets;
    return _assets;
  }

  /// @dev Returns the default supported asset
  function getDefaultSupportedCollateralAsset() external view returns(address _default) {
    _default = assets[0].id;
    assert(_default != address(0));
    return _default;
  }

}","keccak256":"0xad219cbb17654c5dbe7959948dcde82c7bddfad9f92f37a4169e41234e245cbc","license":"MIT"}},"version":1}
