// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import { ERC20Abstract } from "./ERC20Abstract.sol";

/*
    @title QToken is the native token of the Simplifinance platform.
            It is a utility token that gives its holders access to Simplifinance
            products.
        Standard: Customized and ERC20 compatible.
        Type: Deflationary.
        Max Supply: 1_000_000_000.
        Decimal: 18.
*/
contract SimpliToken is ERC20Abstract {
    event Locked(address from, uint256 amount);
    event UnLocked(address from, uint256 amount);

    constructor( 
        address attorney_,
        address reserve_,
        address initTokenReceiver,
        address _ownershipMgr
    ) ERC20Abstract(attorney_, reserve_, initTokenReceiver, _ownershipMgr) { }

    ///@dev Contract accepts no platform coin
    receive() external payable {
        revert("NA");
    }

    ///@dev See IERC20.sol {lockToken}
    function lockToken(address _routeTo, uint256 amount) public toggleFunc(Internal.LOCK) returns (bool) {
        _lock(_msgSender(), _routeTo, amount);

        emit Locked(_msgSender(), amount);
        return true;
    }

    ///@dev See IERC20.sol {unlock}
    function unlockToken(uint256 amount) public toggleFunc(Internal.UNLOCK) returns (bool) {
        _unlock(_msgSender(), amount);

        emit UnLocked(_msgSender(), amount);
        return true;
    }

    ///@dev Burns token of `amount`
    function burn(uint amount) public {
        _burn(_msgSender(), amount);
    }

    /**
        See IERC20.sol {batchTransfer}
        A dynamic transfer utility. 
        Note: The size of the amount array must match that of the 
        account's. Anothe benefit is that each of the addresses on the 
        list can be mapped to different amount dynamically.
     */
    function batchTransfer(
        uint[] memory amounts,
        address[] memory accounts
    ) public override toggleFunc(Internal.BATCH) returns (bool) {
        uint accountSize = accounts.length;
        require(accountSize == amounts.length, "Unequal list");
        for (uint i = 0; i < accountSize; i++) {
            address to = accounts[i];
            uint amount = amounts[i];
            _transfer(_msgSender(), to, amount);
        }
        return true;
    }

    /**
     * @dev See {ERC20-_beforeTokenTransfer}.
     *
     * Requirements:
     *
     * - the contract must not be paused.
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, amount);

        require(!paused(), "ERC20Pausable: token transfer while paused");
    }
}

contracts/implementations/token/TokenDis.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import { OnlyOwner } from "../../abstracts/OnlyOwner.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { Lib } from "../../libraries/Lib.sol";
import { SafeERC20 } from "./SafeERC20.sol";
import { IERC20 } from "../../apis/IERC20.sol";

/**
 * @title InitialTokenReceiver
 * @dev Total supply is minted to this contract and is controlled by a set of addresses
 *  acting as multisig addresses. 
 * To transfer any amount of token out of this contract, the following steps
 * must be peformed:
 * 
 * - INITIATE TRXN: A request to transfer an amount must be made. An event is emitted 
 *      to extract the request Id. Request IDs are unique to another.
 * 
 * - SIGN TRXN: The predefined signers addresses will have to sign the transaction with 
 *      the given request Id. The number of signers needed to validate execution must match 
 *      `quorum` declared as state variable.
 * 
 * - EXECUTE TRXN: Transactions can only be executed if the delay period has passed and are 
 *      are already signed.
 * 
 * Same functionalities can be used to add and remove a new signer, and change quorum. 
 */
contract TokenDistributor is 
    OnlyOwner,
    ReentrancyGuard
{
    using Lib for *;
    using SafeERC20 for IERC20;

    error Pending();
    error AlreadySigned();
    error InvalidRequestId(uint);
    error InsufficientBalance(uint256);

    event Requested(uint reqId, address from);
    event Signer(uint reqId, address from);

    enum Status {NONE, INITIATED, PENDING, EXECUTED}

    /**
     * @notice Transaction type
     * Transaction can be ERC20 or Native.
     * By default, they're ERC20 type.
     * We also use other type from 2 and above to denote the 
     * type of transaction that can be performed by the owner account
     */
    enum Type {ERC20, NATIVE, ADDSIGNER, REMOVESIGNER, SETQUORUM}

    /**
     * @notice Number of signers require to execute a transfer
     */
    uint public quorum;

    /**
     * @dev Unique order ID mapped to each transfer request.
     * Note: Two requests can not have same order ID. 
     */
    uint private requestIDs;

    /**
     * @notice Delay timer.
     * Transfers are executed after the expiration of this period.
     */
    uint public delay;

    struct Request {
        uint256 amount;
        address recipient;
        uint delay;
        address[] executors;
        Status status;
        Type txType;
    }

    address[] private executors;

    /**
     * @dev Transfer orders.
     * Mapping of Ids to Request
     * Note: We assigned slot `0` to add and remove signer.
     * i.e requests[0].
     */
    mapping (uint => Request) private requests;

    /**
     * @dev Signers
     * Mapping of address to bool
     */
    mapping (address => bool) private signers;

    /**
     * @dev Signed requests
     * Mapping of signer to request id to bool
     */
    mapping (address => mapping (uint => bool)) private signed;

    // ERC20 basic token contract held by this contract
    IERC20 public token;

    /**
     * @dev Only signers are allowed
     */
    modifier onlySigner {
        require(signers[_msgSender()], "Not a signer");
        _;
    }

    /**
     * @dev Request Id must not be zero
     * @param reqId : Request Id
     */
    modifier validateRequestId(uint reqId) {
        if(reqId == 0) revert InvalidRequestId(reqId);
        _;
    }

    /**
     * @dev Only when not signed.
     * @param reqId : Request Id
     */
    modifier whenNotSign(uint reqId) {
        address caller = _msgSender();
        if(signed[caller][reqId]) revert AlreadySigned();
        _;
        signed[caller][reqId] = true;
    }

    constructor(
        address _ownershipManager,
        address[] memory _signers, 
        uint8 _quorum
    ) 
        OnlyOwner(_ownershipManager) 
    {
        uint size = _signers.length;
        quorum = _quorum;
        if(size > 0) {
            for (uint i = 0; i < size; i++) {
                _addSigner(_signers[i]);
            }
        }
    }

    receive() external payable {}

    function setToken(IERC20 newToken) 
        public 
        onlyOwner
    {
        address(newToken).cannotBeEmptyAddress();
        token = newToken;
    }

    /**
     * @dev Generate request Id
     * Request Id is generated for all transaction type
     */
    function _generateRequestId() private returns(uint id) {
        requestIDs ++;
        id = requestIDs;
        // id = _type < 2? requestIDs : 0;
    }
    
    /**
     * @dev Check that current request's staus correspond
     * @param _req : Request struct.
     * @param _status : Status to match with.
     * @param errorMessage : Error message.
     */
    function _whenStatus(Request memory _req, Status _status, string memory errorMessage) internal pure {
        require(_req.status == _status, errorMessage);
    }

    /**@dev Initiate transaction. 
        This function can be invoked only by the authorized accounts
        as signers.
        The `_type` parameter is used to select the type of transaction 
        the caller want to perform - a more reason we restrict the `_type` 
        input to number between 0 and 5 otherwise we get out of bound error
        since enums work similar to arrays.
        @param _recipient :     An address to act as the beneficiary of this 
                                transaction.
        @param _amount:         Amount to send as value (in token).
        @param _delayInHours:   If this flag is set to value greater than 0,
                                execution of the proposed transaction will not succeed unless the 
                                delay time has passed.
        @param  _type:  The type of transaction to perform. Transction could be any of the following
                        as set in the `Type` enum:
                        ERC20 (0): Transfer ERC20 token.
                        NATIVE (1): Transfer network asset such as ETH.
                        ADDSIGNER (2): Add more signers to the list.
                        REMOVESIGNER (3): Deactivate the `_recipient` address from as a signers. 
                        SETQUORUM (4): Increase or decrease the number of signatories needed to execute a transaction.
        Note: By default, the caller is deemed to have signed the transaction.
     */
    function initiateTransaction(
        address _recipient,
        uint256 _amount,
        uint16 _delayInHours,
        uint8 _type
    ) public onlySigner {
        require(_type < 5, "Invalid selector");
        uint reqId = _generateRequestId();
        if(_type < 4) {
            require(_recipient != address(0), "Recipient is zero addr");
        }
        requests[reqId].amount = _amount;
        requests[reqId].recipient = _recipient;
        requests[reqId].status = Status.INITIATED;
        requests[reqId].txType = Type(_type);
        unchecked {
            requests[reqId].delay = _now() + (_delayInHours * 1 hours);
            
        }
        address caller = _msgSender();
        requests[reqId].executors.push(caller);
        signed[caller][reqId] = true;

        emit Requested(reqId, caller);
    }

    function _now() internal view returns(uint64) {
        return uint64(block.timestamp);
    }

    /**
     * @dev Sign transactions
     * @param reqId : Request Id.
     * Note: Caller must be one of the signers
     */
    function signTransaction(uint reqId) 
        public 
        onlySigner
        validateRequestId(reqId)
        whenNotSign(reqId)
    {
        Request memory req = requests[reqId];
        _whenStatus(req, Status.INITIATED, "Trxn must be initiated");
        uint currentSigners = req.executors.length;
        require(currentSigners < quorum, "Signers complete");
        address caller = _msgSender();
        requests[reqId].executors.push(caller);
        signed[caller][reqId] = true;
        if((currentSigners + 1) == quorum) {
            requests[reqId].status = Status.PENDING;
        }
    }

    /**
     * @dev Executes pending transaction.
     * @param reqId : Request Id
     * Transaction must be pending in status. When moved to executed, 
     * they have no way to be restored.
     * Only signer accounts can call.
     */
    function executeTransaction(uint reqId) public onlySigner validateRequestId(reqId) nonReentrant {
        Request memory req = requests[reqId];
        _whenStatus(req, Status.PENDING, "Trxn must be initiated");
        if(_now() < req.delay) revert Pending();
        requests[reqId].status = Status.EXECUTED;
        if(req.txType == Type.ERC20) {
            address(token).cannotBeEmptyAddress();
            token.safeTransfer(req.recipient, req.amount);
        } else if(req.txType == Type.NATIVE) {
            uint256 balance = address(this).balance;
            if(balance < req.amount) revert InsufficientBalance(balance);
            (bool success,) = req.recipient.call{value: req.amount}("");
            require(success,"Trxn failed");
        } else if(req.txType == Type.ADDSIGNER) {
            req = requests[reqId];
            delete requests[reqId];
            _addSigner(req.recipient);
        } else if(req.txType == Type.REMOVESIGNER) {
            req = requests[reqId];
            delete requests[reqId];
            _removeSigner(req.recipient);
        } else if(req.txType == Type.SETQUORUM) {
            quorum = req.amount;
        }

    }
    
    /**
     * @dev Remove a signer from the list
     */
    function _removeSigner(address account) private {
        signers[account] = false;
    }

    /**
     * @dev Add a signer to the list
     */
    function _addSigner(address account) private {
        signers[account] = true;
        executors.push(account);
    }

    /**
        @dev Return request struct at the reqId ref.
     */
    function getTransactionRequest(uint reqId) public view returns(Request memory req) {
        return requests[reqId];
    }

    function deposit() public payable {
        require(msg.value > 0, "000/");
    }

    function getExecutors() public view returns(address[] memory) {
        return executors;
    }

}

contracts/libraries/FactoryLibV3.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

// import "hardhat/console.sol";   // For debugging only
import { SafeMath } from "@thirdweb-dev/contracts/external-deps/openzeppelin/utils/math/SafeMath.sol";
import { Counters } from "@thirdweb-dev/contracts/external-deps/openzeppelin/utils/Counters.sol";
import { IERC20 } from "../apis/IERC20.sol";
import { IBankFactory } from "../apis/IBankFactory.sol";
import { IBank } from "../apis/IBank.sol";
import { IFactory } from "../apis/IFactory.sol";
import { C3 } from "../apis/C3.sol";
import { AssetClass } from "../implementations/AssetClass.sol";
import { Utils } from "../libraries/Utils.sol";

/**@dev
  * @param amountExist: Tracks unit contribution i.e values created in each permissionless communities
  * @param pools: Mapping of unitIds to Pool
  * @param pData: Public State variable stats
  * @param epoches : Total pool created to date
  * @param contributors : Mapping of unitIds to group of contributors
  * @param slots : Reverse map of contributors to unitId to slots on the list.
    
  @param indexes: Each amount is mapped to an index. These indexes are used to retrieve the corresponding Amount struct from the 'amount' storage
  * 
*/
struct Data {
  Counters.Counter epoches;

  Counters.Counter cSlots;

  Counters.Counter pastEpoches;

  IFactory.ContractData pData;

  mapping(uint => C3.Contributor[]) cData;

  mapping(uint256 => uint) indexes; // For every unit amount of contribution, there is a corresponding index for retrieving data from the storage.

  mapping(uint => C3.Pool) records; // Past transactions i.e Ended or Canceled pools 

  mapping(uint => C3.Pool) current; // Mapping of unitId to Pool
  
  mapping(address => C3.Point) points;

  mapping(address => mapping(uint256 => C3.Slot)) slots;
}

struct Def {
  bool t;
  bool f;
  uint8 zero;
  uint8 one;
  address zeroAddr;
  uint reserve;
}

library FactoryLibV3 {
  using Utils for *;
  using SafeMath for uint256;
  using Counters for Counters.Counter;

  error CurrentReceiverIsNotADefaulter();
  error APoolWithThisAmountExist();
  error CancellationNotAllowed();
  error TurnTimeHasNotPassed();
  error AmountNotInitialized();
  error OnlyAdminIsAllowed();
  error PoolNotComplete();
  error PoolIsFilled();
  error NoDebtFound();
  error NotAMember();

  /**
   * @dev Verifiy that status is already initialized
  */
  function _isInitialized(Data storage self, uint256 unit, bool secondCheck) internal view {
    C3.Pool memory _p = self.current[_getIndex(self.indexes, unit)];
    if(!_p.status.isInitialized) revert AmountNotInitialized();
    if(secondCheck){
      require(_p.status.value == C3.Status.TAKEN, 'Amount not initialized');
    } 
  }

  /**
   * @dev Verifiy that unit contribution is not initialized, then intialize it.
  */
  function _initializeUnit(Data storage self, uint256 unit) internal returns(C3.ReturnValue memory rv) {
    rv.uId = _getIndex(self.indexes, unit);
    rv.pool = self.current[rv.uId];
    if(!rv.pool.status.isInitialized) {
      rv.pool.status.isInitialized = true;
      self.epoches.increment();
      rv.uId = self.epoches.current();
      rv.pool.uint256s.unitId = rv.uId;
    } else {
      if(rv.pool.status.value == C3.Status.TAKEN) {
        revert APoolWithThisAmountExist();
      }
    }
    self.pastEpoches.increment();
    rv.pool.status.value = C3.Status.TAKEN;
  }

  /**
   * @dev Return the index for a unit amount
  */
  function _getIndex(mapping(uint256 => uint) storage self, uint256 unit) internal view returns(uint index) {
    index = self[unit];
  }

  /**
   * @dev Return the corresponding index for a unit amount of contribution from storage
  */
  function _getCurrentPool(Data storage self, uint256 unit) internal view returns(C3.GetPoolResult memory result) {
    result.uId = _getIndex(self.indexes, unit);
    result.data = self.current[result.uId];
  }

  /**
   * @dev Check that user has given enough approval to spend from their balances
   * @param user : Caller.
   * @param assetInUse : ERC20 currency address to use as contribution base.
   * @param value : Contribution per user.
  */
  function _validateAllowance(
    address user, 
    address assetInUse, 
    uint value
  ) 
    internal 
    view 
  {
    require(IERC20(assetInUse).allowance(user, address(this)) >= value, "FactoryLib: Insufficient allowance");
  }

  function _addUpToBank(address bank, address user, uint rId) internal {
    IBank(bank).addUp(user, rId);
  }

  /**
   * @dev Create a fresh pool
   * @param self: Storage of type `Data`
   * @param cpp: This is a struct of data much like an object. We use it to compress a few parameters
   *              instead of overloading _createPool.
   * @notice We first check that the duraetion given by the admin should not be zero.
   * Note: `.assertChained3` is simply making tripple boolean checks.
   */
  function _createPool(
    Data storage self,
    C3.CreatePoolParam memory cpp,
    address bank,
    address user,
    C3.Router router,
    function (Data storage, C3.ReturnValue memory) internal callback
  ) 
    private 
    returns(C3.ReturnValue memory rv) 
  {
    Def memory _d = _defaults();
    bool(cpp.colCoverage >= 100).assertTrue("Col coverage is too low");
    Utils.assertTrue_2(cpp.duration > _d.zero, cpp.duration <= 720, "Invalid duration"); // 720hrs = 30 days.
    _awardPoint(self.points, user, _d.t);
    self.cSlots.increment();
    rv = _updatePoolSlot(self, cpp, bank, router, _initializeUnit(self, cpp.unit));
    callback(self, rv);
    _addUpToBank(bank, user, rv.rId);
    _validateAllowance(user, cpp.asset, cpp.unit);
    _withdrawAllowanceToBank(user, cpp.asset, cpp.unit, bank);
  }

  function _defaults()
    internal 
    pure 
    returns(Def memory) 
  {
    return Def(true, false, 0, 1, address(0), uint(0));
  }

  ///@dev Returns current timestamp (unix).
  function _now() 
    internal 
    view returns (uint) 
  {
    return block.timestamp;
  }



  /**@notice Send contribution to bank
   * @param user : User/Caller address
   * @param assetInUse: ERC20 token { USD contract address }
   * @param unitContribution: Unit contribution
   * @param bank : Address to hold funds on behalf of the members. The factory generates 
   * a bank by querying StrategyManager. Every address that create a pool must operate a 
   * bank. This process is managed internally for users.  
   */
  function _withdrawAllowanceToBank(
    address user, 
    address assetInUse, 
    uint unitContribution, 
    address bank
  ) 
    private 
  {
    require(IERC20(assetInUse).transferFrom(user, bank, unitContribution), "FactoryLib: Transfer failed");
  }

  /**
   * @dev Initializes the slot for pool user intend to create.
   * @notice To maintain storage orderliness especially when an epoch has ended and we need to 
   * keep record of the pool, we create a slot ahead for the initialized pool so we can move current
   * pool to it when it is finalized.
   */
  function _initializePool(
    Data storage self,
    C3.Pool memory _p, 
    C3.CreatePoolParam memory cpp,
    C3.InterestReturn memory itr,
    address bank,
    uint uId,
    uint24 durInSec,
    C3.Router router
  ) internal view returns(C3.ReturnValue memory rv) {
    Def memory _d = _defaults();
    rv.rId = self.pastEpoches.current();
    uint cSlot = self.cSlots.current();
    rv = C3.ReturnValue(
      C3.Pool({
        uints: C3.Uints(cpp.quorum, _d.zero, cpp.colCoverage, durInSec, cpp.intRate, cSlot, _d.zero, _p.uints.userCount),
        uint256s: C3.Uint256s(itr.fullInterest, itr.intPerSec, cpp.unit, cpp.unit, uId, rv.rId),
        addrs: C3.Addresses(cpp.asset, _d.zeroAddr, bank, cpp.members[0]),
        router: router,
        status: _p.status,
        stage: C3.FuncTag.JOIN
      }),
      uId,
      rv.rId
    );
  }

  /*** @dev Update the storage with pool information
   * @param self: Storage of type `Data`
   * @param cpp: This is a struct of data much like an object. We use it to compress a few parameters
   *              instead of overloading _createPool.
   * @param uId: Unit Id ref/index in storage.
   * @param bank: Strategy contract.
   */
  function _updatePoolSlot(
    Data storage self,
    C3.CreatePoolParam memory cpp,
    address bank,
    C3.Router router,
    C3.ReturnValue memory rv
    // C3.Pool memory pool,
    // uint uId
  ) 
    internal view returns(C3.ReturnValue memory _rv)
  {
    uint24 durInSec = _convertDurationToSec(uint16(cpp.duration));
    _rv = _initializePool(
      self, 
      rv.pool, 
      cpp, 
      cpp.unit.mul(cpp.quorum).computeInterestsBasedOnDuration(cpp.intRate, durInSec, durInSec), 
      bank, 
      rv.uId, 
      durInSec, 
      router
    );
  }

  /**
   * @dev Update storage
   */
  function _callback(Data storage self, C3.ReturnValue memory rv) private {
    self.current[rv.uId] = rv.pool;
  }

  /**@dev Create permissioned band
   * @param self: Storage of type `Data`.
   * @param cpp : Parameter struct
   * Note: Each of the addresses on the members array must an Account instance. Participants must already own an
   * account before now otherwise, execution will not pass.
   * - Admin cannot replicate themselves in a band.
   * - Each of the contributors must have created account before now.
   * - We assume admin should be address in first slot in the members array, so expression evaluates to `if not admin`.
   */
  function createPermissionedPool(
    Data storage self,
    C3.CreatePoolParam memory cpp
  ) 
    internal
    returns (C3.Pool memory _p) 
  {
    Def memory _d = _defaults();
    address bank = _fetchAndValidateBank(cpp.unit, self.pData.bankFactory); //////1
    for(uint i = _d.zero; i < cpp.members.length; i++) {
      address user = cpp.members[i];
      if(i == _d.zero) {
        _p = _createPool(self, cpp, bank, user, C3.Router.PERMISSIONED, _callback).pool;
        _addNewContributor(self, _p.uints.cSlot, _p.uint256s.unit, user, _d.t, _d.t, _d.t);
      } else {
        bool(user != _p.addrs.admin).assertTrue("Admin spotted twice");
        _addNewContributor(self, _p.uints.cSlot, _p.uint256s.unit, user, _d.f, _d.t, _d.f);
        _p = _getCurrentPool(self, _p.uint256s.unit).data; 
      } 
    }
  }

  /**
   * @dev Return bank for user
   * @param bankFactory: StrategyManager contract address
   * @param unit : Caller
   */
  function _getBank(
    address bankFactory, 
    uint256 unit
  ) 
    internal 
    view
    returns(address _bank) 
  {
    _bank = IBankFactory(bankFactory).getBank(unit);
  }

  /**
   * @dev Checks, validate and return bank for the target address.
   * @param unit : Unit contribution.
   * @param bankFactory : StrategyManager contract address.
   */
  function _fetchAndValidateBank(
    uint256 unit,
    address bankFactory
  ) 
    private 
    returns(address bank) 
  {
    bank = _getBank(bankFactory, unit);
    if(bank == address(0)) {
      bank = IBankFactory(bankFactory).createBank(unit);
    }
    assert(bank != address(0));
  }

    /**
   * @dev Add new member to the pool
   * Note: `target` is expected to be an instance of the `SmartBank`
   */
  function _addNewContributor(
    Data storage self, 
    uint cSlot, 
    uint256 unit,
    address user,
    bool isAdmin,
    bool isMember,
    bool sentQuota                                                                                                                                                                              
  ) 
    private 
    returns(
      C3.Contributor memory cData
    )
  {
    uint8 pos = uint8(self.cData[cSlot].length);
    self.cData[cSlot].push();
    self.slots[user][unit] = C3.Slot(pos, isMember, isAdmin);
    self.cData[cSlot][pos] = C3.Contributor(0, 0, 0, 0, 0, 0, user, sentQuota);
    cData = _getProfile(self, unit, user);

    // cData = _addContributor(
    //   self, 
