  /**@dev Update contributor's data
    @param self : Storage of type mapping
    @param up : Parameters
   */
  function _updateUserData(
    Data storage self, 
    C3.UpdateUserParam memory up
  )
    private 
  {
    self.cData[up.cSlot][up.slot.value] = up.cData;
