","keccak256":"0xa80cb30fccbba7c8ff72ac82cc898576a78d4c3031e85676840048f3fe195f5d","license":"MIT"},"contracts/abstracts/OnlyOwner.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import { MsgSender, OwnerShip } from "../implementations/OwnerShip.sol";
import { IOwnerShip } from "../apis/IOwnerShip.sol";

abstract contract OnlyOwner is MsgSender {
    address public ownershipManager;

    constructor(address _ownershipManager)
    {
        _setOwnershipManager(_ownershipManager);
    }

    /**
     * @notice Caller must have owner role before execeution can proceed.
     * The 'errorMessage' argument can be used to return error specific to 
     * a context e.g function call. 
     * @param errorMessage : Custom error message
     */
    modifier onlyOwner(string memory errorMessage) {
        address mgr = ownershipManager;
        require(mgr != address(0), "OnlyOwner: Manager not set");
        require(IOwnerShip(mgr).isOwner(_msgSender()), errorMessage);
        _;
    }

    function _setOwnershipManager(
        address newManager
    )
        private
    {
        ownershipManager = newManager;
    }

    /**
     * Set Ownership manager
     * @param newManager : New manager address
     */
    function setOwnershipManager(
        address newManager
    )
        public
        onlyOwner("OnlyOwner: Not permitted")
        returns(bool)
    {
        _setOwnershipManager(newManager);
        return true;
    }
}","keccak256":"0x69c663365e2cf43d13d18b4926714078df66472a2d0aca7988dae04b61feafb6","license":"MIT"},"contracts/apis/Common.sol":{"content":"// SPDX-License-Identifier: MIT

import { Counters } from "@thirdweb-dev/contracts/external-deps/openzeppelin/utils/Counters.sol";
pragma solidity 0.8.24;

interface Common {
  /**
   * @dev Tags/Placeholders for functions available in the implementation contract.
   */

  enum FuncTag {
    JOIN, 
    GET, 
    PAYBACK, 
    WITHDRAW,
    CANCELED,
    ENDED
  }

  ////////////////////////////////////////////////////////// V2
  enum Status { AVAILABLE, TAKEN }

  enum Router { PERMISSIONLESS, PERMISSIONED }

  struct Unit {
    bool isInitialized;
    Status status;
  }

  struct Pool {
    Counters.Counter userCount;
    Uints uints;
    Uint256s uint256s;
    Addresses addrs;
    uint allGh;
    Contributor[] cData;
    Router router;
    FuncTag stage;
  }

  struct Point {
    uint contributor;
    uint creator; 
  }

  /**
   *  @dev Data for each pool. 
   *    Note: We use the term `allGh` to denote when every participant in a 
   *    group had get financed.
   *  @param isMember : Whether user is a member or not
   *  @param isAdmin: Whether user is an admin or not i.e the initiator.
   *  @param payDate: The data in future on which the borrowed fund must be retured
   *  @param turnTime : This is a period or interval between the last received and the next.
   *  @param owings: Total debts owed by the last fund recipient.
   *  @param colBals: Collateral balances of the last recipient.
   *  @param hasGH : Whether an user/current msg.sender has received or not.
   *  @param id : Address of the last recipient.  
   */
  struct Contributor {
    uint durOfChoice;
    uint expInterest;
    uint payDate;
    uint turnTime;
    uint loan;
    uint colBals;
    address id;
    bool sentQuota;
  }

  // struct Rank {
  //   bool admin;
  //   bool member;
  // }

  
  struct UpdateMemberDataParam {
    uint24 durOfChoice;
    address expected;
    uint256 unit;
    uint uId;
    uint fee;
    uint msgValue;
    uint xfiUSDPriceInDecimals;
    Pool pool;
  }

  struct AddTobandParam {
    uint unit;
    bool isPermissioned;
  }

  struct GetPoolResult {
    Pool data;
    uint uId;
  }

  struct PaybackParam {
    uint unit;
    address user;
  }

  /**
    @dev Structured data types to convey parameters to avoid Stack too deep error.
    @param quorum : The maximum number of users that can form a contribution group.
    @param duration : The number of days the contribution contract will expires. It should be 
                      specified in hour.
    @param colCoverage : Collateral Coverate Ratio : The ratio of collateral a member must hold 
                  in order to be able to get financed. This should be specified in percentage i.e 
                  if raw ccr is 1.2 , actual ccr should be 1.2 * 100 = 120. It is pertinent to be
                  mindful how this works in our protocol. Even if ccr is 1 indicating that contributor
                  must hold at least 100% of collateral in their wallet before they can GF, it must 
                  be rendered in input section as 1 * 100. 100 is the minimum admins of bands
                  can give as collateral coverage ratio.
    @param value : The total value of pooled fund.
    @param members : List of members in a group.
    @param intRate : The rate of interest to charge for the duration of use of the fund.
    @param asset : The contract address of an approved assets in this group. 
                    @notice The pooled asset of this group is denominated in this currency. 
  */
  struct CreatePoolParam {
    uint16 intRate;
    uint8 quorum;
    uint16 duration;
    uint24 colCoverage; 
    uint unit;
    address[] members;
    address asset;
  }

  /**
   *  @notice Structured types - uint256
   *  @param unit : Unit contribution.
   *  @param currentPool : Total contributed to date.
   */
  struct Uint256s {
    uint fullInterest;
    uint intPerSec;
    uint256 unit;
    uint256 currentPool;
    uint unitId;
    // uint slot;
  }

  /**
   *  @notice Structured types - unit less than uint124
   *  @param intRate : Rate of interest per duration. 
   *  @param quorum : The maximum number of users that can form a contribution group.
   *  @param selector : This is like the hand or ticker of a clock that is used to select
   *                    the next contributor to get finance.
   *  @param colCoverage : Collateral Coverate Ratio : The ratio of collateral a member must hold 
                  in order to be able to get financed.
      @param duration : The number of days the contribution contract will expires.
   */
  struct Uints {
    uint quorum;
    uint selector;
    uint colCoverage;
    uint duration;
    uint intRate;
  }
  
  /**
   * @notice Structured types - Address
   * @param asset : Contract address of the asset in use.
   * @param lastPaid: Last contributor who got finance.
   * @param bank : Strategy for each pool or epoch. See Strategy.sol for more details.
   * @param admin : Pool creator.
   * 
   */
  struct Addresses {
    address asset;
    address lastPaid;
    address bank;
    address admin;
  }
  

  struct CreatePoolReturnValue {
    Pool pool;
    Contributor cData; 
  }

  struct InterestReturn {
    uint fullInterest;
    uint intPerSec;
    uint intPerChoiceOfDur;
  }

  struct CommonEventData {
    Pool pool;
    uint debtBal;
    uint colBal;
  }

  // struct Contributor {
  //   Contributor cData;
  //   // Rank rank;
  //   // uint8 slot;
  // }

  struct Balances {
    uint xfi;
    uint erc20;
  }

  struct DebtReturnValue {
    uint debt;
    uint pos;
  }

  struct SwapProfileArg {
    Slot expSlot;
    address expCaller;
    address actCaller;
    uint unit;
    uint uId;
    Contributor expcData;
  }

  struct Slot {
    uint value;
    bool isMember;
    bool isAdmin;
  }

  struct UpdateUserParam {
    Contributor cData;
    Slot slot;
    uint uId;
    uint256 unit;
    address user;
  }
  
  // error UpdateStrategyError();
  error CollateralCoverageCannotGoBelow_100(uint24 ccr);

}","keccak256":"0xd9f85b27e91e5617607535edaf1a118d6dbf30862a1960795ca983a59242e82a","license":"MIT"},"contracts/apis/IBank.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import { Common } from "./Common.sol";

interface IBank {
  error NotACustomer(address);
  error AlreadyACustomer(address);
  error ZeroWithdrawable();
  error UserDoesNotHaveAccess();
  error NoFeeToWithdraw();
  error TokenAddressIsZero();
  
  function addUp(address user) external;
  function borrow(
    address user, 
    address asset, 
    uint256 loan, 
    uint fee, 
    uint256 calculatedCol
  ) 
    external 
    payable 
    returns(uint);

  function payback(
    address user, 
    address asset, 
    uint256 debt,
    uint256 attestedInitialBal,
    bool allGH,
    Common.Contributor[] memory cData,
    bool isSwapped,
    address defaulted
  ) external payable;

  function depositCollateral() external payable returns(bool);
  function cancel(address user, address asset, uint erc20Balances) external;
  function withdrawFee(address recipient, address asset) external;
  function getData() external view returns(ViewData memory);

  struct ViewData {
    uint totalClients;
    uint aggregateFee;
  }

  struct ViewUserData {
    bool access;
    Collateral collateral;
  }

  struct Collateral {
    uint balance;
    uint withdrawable;
  }
}","keccak256":"0xc3cd17a304775828d46aabc2ac292e8ce236e226a25fe708ef212aa45859975d","license":"MIT"},"contracts/apis/IBankFactory.sol":{"content":"// SPDX-License-Identifier: MIT
