pragma solidity 0.8.24;

import { IOwnerShip } from "../apis/IOwnerShip.sol";
/**
 * @title MsgSender 
 * @author Simplifi (Bobeu)
 * @notice Non-deployable contract simply returning the calling account.
 */
abstract contract MsgSender {
    function _msgSender() internal view returns(address sender) {
        sender = msg.sender;
    }
}

/**
 * @title OwnerShip 
 * @author Simplifi (Bobeu)
 * @notice Standalone contract for managing ownership in Simplifi protocol.
 */
contract OwnerShip is IOwnerShip, MsgSender{
    /**
     * @notice Number of owners.
     */
    uint public ownersCount;

    /**
     * @notice Addresses with ownership permission.
     */
    
    mapping (address => bool) private _isOwner;

    /**
     * @notice Mapping of ownersCount to addresses.
     * A valid id will return a mapped owner.
     */
    mapping (uint => address) public owners;

    /**
     * @dev Only owner is allowed.
     */
    modifier onlyOwner {
        require(_isOwner[_msgSender()], "Oop! Caller is not recognized");
        _;
    }

    constructor() {
        _setOwner(_msgSender(), true);
    }

    /**
     * @dev Returns owner variable.
     */
    function _getOwner(uint ownerId) 
        internal 
        view 
        returns(address _owner) 
    {
        _owner = owners[ownerId];
    }

    /**
     * @dev Add or remove target address as owner.
     * @param target: Target address.
     * @notice 'target' parameter must not be empty.
     */
    function _setOwner(
        address target,
        bool add
    ) 
        private 
    {
        require(target != address(0), "Simplifi OwnerShip: 'target' parameter is empty");
        add? (_isOwner[target] = true, ownersCount ++) : (_isOwner[target] = false, ownersCount --);
    }

    /**
     * @dev Add a new owner address
     * @param newOwners: New owners
     * @notice Only address with owner permission can add another owner.
     */
    function setPermission(
        address[] memory newOwners
    ) 
        external
        onlyOwner
        returns(bool) 
    {
        bool rt = true;
        for(uint r = 0; r < newOwners.length; r++) {
            _setOwner(newOwners[r], rt);
        }
        return rt;
    }

    /**
     * @dev Remove an address as owner.
     * @param target: Target address
     * @notice Only address with owner permission can remove another owner.
     */
    function removeOwner(
        address target
    ) 
        external
        onlyOwner
        returns(bool) 
    {
        _setOwner(target, false);
        return true;
    }
    /**
     * @dev An owner can renounce their ownership. This however will not leave the
     * contract empty without an owner. There must be at least one owner left.
     * @notice Only address with owner permission can renounce ownership.
     */
    function renounceOwnerShip() 
        external
        onlyOwner
        returns(bool) 
    {
        require(ownersCount > 1, "At least 2 owners is required to leave"); 
        _setOwner(_msgSender(), false);
        return true;
    }

    /**
     * @dev Returns owner variable.
     * Can be called externally by contracts.
     * @param ownerId : Owner Id. 
     */
    function getOwner(
        uint ownerId
    ) 
        external 
        view 
        returns(address) 
    {
        return _getOwner(ownerId);
    }

    /**
     * @dev Check if target is an owner.
     * @param target : Target address.
     */
    function isOwner(
        address target
    )
        external
        view 
        returns(bool) 
    {
        return _isOwner[target];
    }
}","keccak256":"0xecca6e7a2c2684e193fcb16ac131d1b2895f981862969101d96d94616db70c0c","license":"MIT"},"contracts/implementations/strategies/Bank.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import "hardhat/console.sol";
import { SafeMath } from "@thirdweb-dev/contracts/external-deps/openzeppelin/utils/math/SafeMath.sol";
import { ReentrancyGuard } from "@thirdweb-dev/contracts/external-deps/openzeppelin/security/ReentrancyGuard.sol";
import { SafeCallERC20, IERC20 } from "../../libraries/SafeCallERC20.sol";
import { IBank } from "../../apis/IBank.sol";
import { Common } from "../../apis/Common.sol";
import { OnlyOwner } from "../../abstracts/OnlyOwner.sol";

// Stores and tracks total USD locked in an epoch
// When stage in: 
/**
 *  CREATE & JOIN: 
 *      - From Factory, we transfer USD to bank, then inform bank to keep track of it against the epoch.
 *      Ex. Bob and Alice joined an epoch with unit contribution of $2, quorum is 2, expected contribution will be $4 hence $4 is sent
 *      to Stretegy.
 *  GET-FINANCE:
 *      - Withdraw USD to the next contributor. i.e $4 is sent to a contributor.
 *      - Takes fee if applicable, and forward to 'feeReceiver'.
 *      - Retrieves collateral from the borrower (in XFI) to corresponding bank.
 *  PAYBACK:
 *      - Return USD (with interest) to bank. i.e Bob returned $4 plus interest accrued.
 *      - Forward collateral balance to the contributor.
 *      - If all the contributors have GF:
 *            - Divide total returns (i.e USD) in an epoch by number of contributors.
 *            - Set allowance to withdraw the result for each contributor.
*/
contract Bank is IBank, OnlyOwner, ReentrancyGuard {
  using SafeMath for uint;

  uint private clients;

  uint private aggregateFee;

  mapping (address => bool) private access;

  mapping (address => Collateral) private collateralBalances;

  ///@dev Only users with access role are allowed
  modifier hasAccess(address user) {
    if(!access[user]) revert UserDoesNotHaveAccess();
    _;
  }

  /**
   * @dev Initializes state variables.
   * OnlyOwner function.
   */
  constructor (address _ownershipManager) OnlyOwner(_ownershipManager)  {}

  receive() external payable {
    _depositCollateral(msg.value);
  }

  /**
   * @dev Registers new user
   * @param user New user
   */
  function _registerClient(address user) private {
    if(access[user]) revert AlreadyACustomer(user);
    access[user] = true;
  }

  /**
   * @dev Implementation of IBank.addUp
   * See IBank.addUp
  */
  function addUp(address user) 
    external
    onlyOwner("Bank - addUp: Not permitted")
  {
    clients ++;
    _registerClient(user);
  }

  /**
   * @dev UnLocks collateral balances
   * @param user Existing user
   */
  function _unRegisterClient(address user) private {
    if(!access[user]) revert NotACustomer(user);
    access[user] = false;
  }

  /**
   * @dev Approve spender contributor 'to' to spend from contract's balance
   * @param to : Contributor
   * @param asset : Currency in use
   * @param amount : Value
   * @notice Consideration is not given to the previous allowances given to users.
   *          Users are expected to withdraw allowances immediately they complete 
   *          related transactions such as 'getFinance'.
   */
  function _setAllowance(
    address to, 
    address asset, 
    uint256 amount
  ) 
    private 
  {
    IERC20(asset).approve(to, amount);
  }

  function _tryRoundUp(address asset, Common.Contributor[] memory cData) internal {
    uint erc20bBalances = IERC20(asset).balanceOf(address(this));
    uint fees = aggregateFee;
    if(erc20bBalances > fees) {
      erc20bBalances -= fees;
      fees = erc20bBalances.div(cData.length);
      for(uint i = 0; i < cData.length; i++) {
        address user = cData[i].id;
        _setAllowance(user, asset, fees);
      }
    }
    clients = 0;
  }

  /**
   * @dev Get Finance - We send USD to user and accept collateral.
   * @param user : Beneficiary.
   * @param loan : Amount to receive as loan.
   */
  function borrow(
    address user, 
    address asset, 
    uint256 loan, 
    uint fee, 
    uint256 calculatedCol
  ) 
    external 
    payable 
    onlyOwner("Bank: Not Permitted")
    returns(uint) 
  {
    assert(asset != address(0) && user != address(0));
    Collateral memory col = collateralBalances[user];
    uint primaryBal = col.balance.add(msg.value);
    if(calculatedCol <= primaryBal){
      col.withdrawable = col.withdrawable.add(primaryBal.sub(calculatedCol));
    } else {
      uint agBalance = col.balance.add(msg.value).add(col.withdrawable);
      require(agBalance >= calculatedCol, "Aggregate balances is insufficient");
      col.withdrawable = agBalance.sub(calculatedCol);
    }
    col.balance = calculatedCol;
    collateralBalances[user] = col;
    primaryBal = loan;
    // console.log("Allowance: ", loan);
    if(fee > 0) {
      unchecked {
        aggregateFee += fee;
      }
      if(loan > fee){
        unchecked {
          primaryBal -= fee;
        }
      }

    }
    _setAllowance(user, asset, primaryBal);
    // console.log("Allowance: ", loan);
    // console.log("aggregateFe: ", aggregateFee);
    // console.log("fee: ", fee);
    return loan;
  }

  function payback(
    address user, 
    address asset, 
    uint256 debt,
    uint256 attestedInitialBal,
    bool allGF, 
    Common.Contributor[] memory cData,
    bool isSwapped,
    address defaulted
  ) external payable onlyOwner("Bank: Not Permitted"){
    assert(IERC20(asset).balanceOf(address(this)) >= (attestedInitialBal + debt));
    Collateral memory col = collateralBalances[user];
    if(isSwapped) {
      col = collateralBalances[defaulted];
      delete collateralBalances[defaulted];
      collateralBalances[user] = col; 
      _unRegisterClient(defaulted);
      _registerClient(user);

    }
    collateralBalances[user] = Collateral(0, col.withdrawable.add(col.balance));
    if(allGF) { _tryRoundUp(asset, cData); }
  }

  /**
   *  @dev Withdraw Collateral. 
   */
  function withdrawCollateral() 
    public 
    hasAccess(_msgSender())
    nonReentrant
    returns(bool) 
  {
    address caller = _msgSender();
    Collateral memory col = collateralBalances[caller];
    uint balances = address(this).balance;
    if(col.withdrawable == 0) revert ZeroWithdrawable();
    if(col.balance == 0){
      _unRegisterClient(caller);
      delete collateralBalances[caller];
    } else {
      collateralBalances[caller].withdrawable = 0;
    }
    require(balances >= col.withdrawable, "Balance Anomaly");
    payable(caller).transfer(col.withdrawable);
    return true;
  }

  function depositCollateral() 
    external 
    payable 
    returns(bool)
  {
    _depositCollateral(msg.value);
    return true;
  }

  /**
   * @notice User can deposit collateral ahead of time
   * @param amount msg.value
   */
  function _depositCollateral(uint amount) 
    internal
    hasAccess(_msgSender())
  {
    unchecked {
      collateralBalances[_msgSender()].withdrawable += amount;
    }
  }

  function cancel(
    address user, 
    address asset, 
    uint erc20Balances
  ) external {
    _setAllowance(user, asset, erc20Balances);
    _unRegisterClient(user);
  }

  function withdrawFee(
    address recipient, 
    address asset
  ) 
    external 
    nonReentrant 
    onlyOwner("Bank: Not permitted") 
  {
    uint fees = aggregateFee;
    if(fees == 0) revert NoFeeToWithdraw();
    if(asset == address(0)) revert TokenAddressIsZero();
    aggregateFee = 0;
    IERC20(asset).transfer(recipient, fees);
  }

  function getData() external view returns(ViewData memory) {
    return ViewData(clients, aggregateFee);
  }

  function getUserData(address user) external view returns(ViewUserData memory) {
    return ViewUserData(
      access[user],
      collateralBalances[user]
    );
  }

}","keccak256":"0x3a320b5a889f335593081cb5146f1a967f9efd5e530226acdf624240652ac112","license":"MIT"},"contracts/implementations/strategies/BankFactory.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import { IBankFactory } from "../../apis/IBankFactory.sol";
import { Bank } from "./Bank.sol";
import { OnlyOwner } from "../../abstracts/OnlyOwner.sol";

/**@title SmartBankAdmin: A standalone contract that manages bank creation, 
   deletion, read and write data.

   Author: Simplifinance
 */
contract BankFactory is IBankFactory, OnlyOwner {
  // using Clones for address;

  // Bank count 
  uint public totalBanks;

/**
 * @dev List of Strategies and their keys 
 */
  // BankData[] private strategies;

 /**
 * @dev Mapping of addresses to strategies.
 * Also used as reverse map of strategies to status.
 */
  mapping(uint256 => address) private bankMap;

  constructor (
    address _ownershipManager
  ) OnlyOwner(_ownershipManager) {}

  receive() 
    external 
    payable 
  {
    revert();
  }
  
  /**@dev Return if account owns a bank or not
  */
  function _hasBank(
    uint256 unit
  ) 
    internal 
    view 
    returns (bool) 
  {
    return bankMap[unit] != address(0);
  }

  // Returns smartBank for 'user'
  function _getBank(
    uint256 unit
  ) 
    internal 
    view returns(address) 
  { 
    return bankMap[unit];
  }
  
  // function _getBank(
  //   address user
  // ) 
  //   internal 
  //   view returns(address) 
  // { 
  //   return bankMap[user];
  // }
  
  /**@dev Create a new bank.
   * @notice 'unit' should not own a bank before now.
   *          only address with owner permission can call.
  */
  function createBank(
    uint256 unit
  )
    external
    onlyOwner("BankFactory - createBank: Not permitted")
    returns(address _bank) 
  {
    if(!_hasBank(unit)){
      _bank = _createBank(unit);
    } else {
      _bank = _getBank(unit);
    }
    return _bank;
  }

  /**
    * @param unit : Amount
   * 
   * @notice Even if user is trying to rekey or upgrade smartbank, same amount of fee is required
   * for successful upgrade.
   */
  function _createBank(
    uint256 unit
  ) 
    private 
    returns(address bank) 
  {
    totalBanks ++;
    // address ssi = instance;
    // bank = ssi.cloneDeterministic(keccak256(abi.encodePacked(totalBanks, caller)));
    bank = address(new Bank(ownershipManager));
    _updateBank(unit, bank);
  }

  /**
   * Update storage with the new Bank instance : {internal}
   * @param unit : Unit amount 
   * @param bank : New Bank address
   */
  function _updateBank(
    uint256 unit, 
    address bank
  ) 
    private 
  {
    bankMap[unit] = bank;
  }

  /// Returns then bank for 'unit'
  /// @param unit : Unit amount
  function getBank(
    uint unit
  ) 
    external 
    view 
    returns(address) 
  { 
    return _getBank(unit);
  }
}
","keccak256":"0x26d24fd98dc604c6b31b9c51a91deccd47c3a4e15afa0e32cf9d3fdbdd1e5023","license":"MIT"},"contracts/libraries/SafeCallERC20.sol":{"content":"  // SPDX-License-Identifier: MIT

pragma solidity 0.8.24;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { Address } from "@openzeppelin/contracts/utils/Address.sol";

library SafeCallERC20 {
  using Address for address;

  function safeTransfer(
    IERC20 token,
    address to,
    uint256 value
  ) internal {
    _callOptionalReturn(token, abi.encodeWithSelector(token.transfer.selector, to, value));
  }

  function safeTransferFrom(
    IERC20 token,
    address from,
    address to,
    uint256 value
  ) internal {
    _callOptionalReturn(token, abi.encodeWithSelector(token.transferFrom.selector, from, to, value));
  }

  function safeIncreaseAllowance(
    IERC20 token,
    address spender,
    uint256 value
  ) internal {
    uint256 newAllowance = token.allowance(address(this), spender) + value;
    _callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, newAllowance));
  }

  function safeDecreaseAllowance(
    IERC20 token,
    address spender,
    uint256 value
  ) internal {
    unchecked {
      uint256 oldAllowance = token.allowance(address(this), spender);
      require(oldAllowance >= value, "SafeERC20: decreased allowance below zero");
      uint256 newAllowance = oldAllowance - value;
      _callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, newAllowance));
    }
  }

  /** Imported from Openzeppelin
   * @dev Imitates a Solidity high-level call (i.e. a regular function call to a contract), relaxing the requirement
   * on the return value: the return value is optional (but if data is returned, it must not be false).
   * @param token The token targeted by the call.
   * @param data The call data (encoded using abi.encode or one of its variants).
   */
  function _callOptionalReturn(IERC20 token, bytes memory data) private {
    // We need to perform a low level call here, to bypass Solidity's return data size checking mechanism, since
    // we're implementing it ourselves. We use {Address.functionCall} to perform this call, which verifies that
    // the target address contains contract code and also asserts for success in the low-level call.

    bytes memory returndata = address(token).functionCall(data);
    if (returndata.length > 0) {
      // Return data is optional
      require(abi.decode(returndata, (bool)), "SafeERC20: ERC20 operation failed");
    }
  }
}","keccak256":"0x830e1648c71241b0d5272ba942c8898c38a3ce195e230c5f6d66a64aaff12a92","license":"MIT"},"hardhat/console.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

library console {
    address constant CONSOLE_ADDRESS =
        0x000000000000000000636F6e736F6c652e6c6f67;

    function _sendLogPayloadImplementation(bytes memory payload) internal view {
        address consoleAddress = CONSOLE_ADDRESS;
        /// @solidity memory-safe-assembly
        assembly {
            pop(
                staticcall(
                    gas(),
                    consoleAddress,
                    add(payload, 32),
                    mload(payload),
                    0,
                    0
                )
            )
        }
    }

    function _castToPure(
      function(bytes memory) internal view fnIn
    ) internal pure returns (function(bytes memory) pure fnOut) {
        assembly {
            fnOut := fnIn
        }
    }

    function _sendLogPayload(bytes memory payload) internal pure {
        _castToPure(_sendLogPayloadImplementation)(payload);
    }

    function log() internal pure {
        _sendLogPayload(abi.encodeWithSignature("log()"));
    }

    function logInt(int256 p0) internal pure {
        _sendLogPayload(abi.encodeWithSignature("log(int256)", p0));
    }

    function logUint(uint256 p0) internal pure {
        _sendLogPayload(abi.encodeWithSignature("log(uint256)", p0));
    }

    function logString(string memory p0) internal pure {
        _sendLogPayload(abi.encodeWithSignature("log(string)", p0));
    }

    function logBool(bool p0) internal pure {
        _sendLogPayload(abi.encodeWithSignature("log(bool)", p0));
    }

    function logAddress(address p0) internal pure {
        _sendLogPayload(abi.encodeWithSignature("log(address)", p0));
    }

    function logBytes(bytes memory p0) internal pure {
        _sendLogPayload(abi.encodeWithSignature("log(bytes)", p0));
    }

    function logBytes1(bytes1 p0) internal pure {
        _sendLogPayload(abi.encodeWithSignature("log(bytes1)", p0));
    }

    function logBytes2(bytes2 p0) internal pure {
        _sendLogPayload(abi.encodeWithSignature("log(bytes2)", p0));
